// App.js
import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Outlet, useNavigate } from 'react-router-dom';
import AllProductsPage from './components/AllProductsPage';
import AboutPage from './components/AboutPage';
import Main from './components/Main.js';
import ProductsPage from './components/'
import { ProductProvider } from './context/ProductContext';

class App extends Component {
  constructor() {
    super();
    this.state = {
      isProductsActive: false,
    };
  }

  handleProductsClick = () => {
    // Navigate to the Products route and set the active state
    const { history } = this.props;
    history.push('/products');
    this.setState({ isProductsActive: true });
  };

  render() {
    const { isProductsActive } = this.state;

    return (
      <ProductProvider>
        <Router>
        <div>
          <nav>
            <ul style={{display:"flex",padding:"10px",margin:"10px"}}>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/about">About</Link>
              </li>
              <li>
                <Link to="/products" >
                  Products
                </Link>
              </li>
            </ul>
          </nav>

          <Routes>
            <Route path="/" element={<Main />} />
            <Route path="/about" element={<AboutPage />} />
            <Route path="/products" element={<ProductsPage  />} />
          </Routes>
        </div>
      </Router>
      </ProductProvider>
    );
  }
}

export default App;
